function Invoke-SharpShares
{

    [CmdletBinding()]
    Param (
        [Parameter(Position = 0, Mandatory = $true)]
        [ValidateNotNullorEmpty()]
        [String]
        $Command

    )
    $base64binary=""
    $RAS = [System.Reflection.Assembly]::Load([Convert]::FromBase64String($base64binary))
    [SharpShares.Program]::Main($Command.Split(" "))
}
